\c bank_client;


GRANT CONNECT ON DATABASE bank_client TO readonly_user;
GRANT USAGE ON SCHEMA public TO readonly_user;
GRANT SELECT ON ALL TABLES IN SCHEMA public TO readonly_user;


CREATE TABLE google_ads (
    "Date" DATE,
    "Campaign" VARCHAR(255),
    "Ad_Group" VARCHAR(255),
    "Keyword" VARCHAR(255),
    "Spend" DECIMAL(10, 2),
    "Impressions" INTEGER,
    "Clicks" INTEGER,
    "Conversions" INTEGER,
    "Revenue" DECIMAL(10, 2)
);

CREATE TABLE meta (
    platform VARCHAR(255),
    date DATE,
    campaign VARCHAR(255),
    campaign_group VARCHAR(255),
    campaign_objective VARCHAR(255),
    adset VARCHAR(255),
    ad VARCHAR(255),
    ad_placement VARCHAR(255),
    ad_category VARCHAR(255),
    ad_video_length VARCHAR(50),
    impressions INTEGER,
    clicks INTEGER,
    spend DECIMAL(10, 2),
    landing_page_views INTEGER,
    link_clicks INTEGER,
    video_views_to_3_seconds INTEGER,
    video_plays INTEGER,
    video_views_to_25_percent INTEGER,
    video_views_to_50_percent INTEGER,
    video_views_to_75_percent INTEGER,
    video_views_to_100_percent INTEGER,
    purchase_revenue DECIMAL(10,2)
);

CREATE TABLE pr_coverage_tracker (
    client VARCHAR(255),
    date DATE,
    campaign_release VARCHAR(255),
    research VARCHAR(255),
    creative VARCHAR(255),
    url TEXT,
    publication VARCHAR(255),
    sector VARCHAR(255),
    citation BOOLEAN,
    followed_link BOOLEAN,
    no_follow_link BOOLEAN,
    domain_authority INTEGER,
    estimated_views INTEGER,
    tier VARCHAR(50),
    syndicated BOOLEAN,
    brand VARCHAR(255),
    month INTEGER,
    week INTEGER
);








